// LEFT JOIN "product_size" AS ps ON ps.product_item_id = pi.id
func (q *Queries) SearchProductItemsOld(ctx context.Context, arg SearchProductItemsOldParams) ([]*SearchProductItemsOldRow, error) {
	rows, err := q.db.Query(ctx, searchProductItemsOld, arg.Limit, arg.Query)
	if err != nil {
		return nil, err
	}
	defer rows.Close()
	items := []*SearchProductItemsOldRow{}
	for rows.Next() {
		var i SearchProductItemsOldRow
		if err := rows.Scan(
			&i.ID,
			&i.ProductID,
			&i.ImageID,
			&i.ColorID,
			&i.ProductSku,
			&i.Price,
			&i.Active,
			&i.CreatedAt,
			&i.UpdatedAt,
			&i.Name,
			&i.Description,
			&i.CategoryID,
			&i.BrandID,
			&i.CategoryName,
			&i.ParentCategoryID,
			&i.CategoryImage,
			&i.BrandName,
			&i.BrandImage,
			&i.ParentProductActive,
			&i.QtyInStock,
			&i.ProductImage1,
			&i.ProductImage2,
			&i.ProductImage3,
			&i.ColorValue,
			&i.TotalCount,
			&i.CategoryPromoID,
			&i.CategoryPromoName,
			&i.CategoryPromoDescription,
			&i.CategoryPromoDiscountRate,
			&i.CategoryPromoActive,
			&i.CategoryPromoStartDate,
			&i.CategoryPromoEndDate,
			&i.BrandPromoID,
			&i.BrandPromoName,
			&i.BrandPromoDescription,
			&i.BrandPromoDiscountRate,
			&i.BrandPromoActive,
			&i.BrandPromoStartDate,
			&i.BrandPromoEndDate,
			&i.ProductPromoID,
			&i.ProductPromoName,
			&i.ProductPromoDescription,
			&i.ProductPromoDiscountRate,
			&i.ProductPromoActive,
			&i.ProductPromoStartDate,
			&i.ProductPromoEndDate,
		); err != nil {
			return nil, err
		}
		items = append(items, &i)
	}
	if err := rows.Err(); err != nil {
		return nil, err
	}
	return items, nil
}

const updateProductItem = `-- name: UpdateProductItem :one
UPDATE "product_item"
SET
product_sku = COALESCE($1,product_sku),
image_id = COALESCE($2,image_id),
color_id = COALESCE($3,color_id),
price = COALESCE($4,price),
active = COALESCE($5,active),
updated_at = now()
WHERE id = $6
AND product_id = $7
RETURNING id, product_id, image_id, color_id, product_sku, price, active, created_at, updated_at
`

type UpdateProductItemParams struct {
	ProductSku null.Int    `json:"product_sku"`
	ImageID    null.Int    `json:"image_id"`
	ColorID    null.Int    `json:"color_id"`
	Price      null.String `json:"price"`
	Active     null.Bool   `json:"active"`
	ID         int64       `json:"id"`
	ProductID  int64       `json:"product_id"`
}

// qty_in_stock = COALESCE(sqlc.narg(qty_in_stock),qty_in_stock),
// size_id = COALESCE(sqlc.narg(size_id),size_id),
func (q *Queries) UpdateProductItem(ctx context.Context, arg UpdateProductItemParams) (*ProductItem, error) {
	row := q.db.QueryRow(ctx, updateProductItem,
		arg.ProductSku,
		arg.ImageID,
		arg.ColorID,
		arg.Price,
		arg.Active,
		arg.ID,
		arg.ProductID,
	)
	var i ProductItem
	err := row.Scan(
		&i.ID,
		&i.ProductID,
		&i.ImageID,
		&i.ColorID,
		&i.ProductSku,
		&i.Price,
		&i.Active,
		&i.CreatedAt,
		&i.UpdatedAt,
	)
	return &i, err
}
